import {Routes, Route, useNavigate} from 'react-router-dom';
import './App.css';
import LandingPage from './pages/landing';
import MainPage from './pages/main';
import NewGamePage from './pages/new_game';
import JoinGamePage from './pages/join_game';
import { useState } from "react";
import { io } from "socket.io-client";


function App() {
  // player names
  const [players, set_players] = useState(["Player 1", "Player 2", "Player 3"]);
  const socket = io('http://localhost:5678');

  const setPlayers = (players) => {
    set_players(players);
  }

  const navigate = useNavigate();

  const navigateToMain = () => {
    navigate('/main');
  }

  const navigateToLanding = () => {
    navigate('/');
  }

  const navigateToNewPage = () => {
    navigate('/new_game');
  }

  const navigateToJoinGame = () => {
    navigate('/join_game');
  }

  return (
    <div>
      <Routes>
        <Route exact path="/" element={<LandingPage nav={[navigateToNewPage, navigateToJoinGame]} setPlayers={setPlayers}/>}/>
        <Route exact path="/main" element={<MainPage players={players} nav={navigateToLanding} />}/>
        <Route exact path="/new_game" element={<NewGamePage />}/>
        <Route exact path="/join_game" element={<JoinGamePage />}/>
      </Routes>
    </div>
  )
}

export default App;
