import {Routes, Route, useNavigate} from 'react-router-dom';
import './App.css';
import LandingPage from './pages/landing';
import MainPage from './pages/main';
import NewGamePage from './pages/new_game';
import JoinGamePage from './pages/join_game';
import {SocketContext, socket} from './src/context/socket';


function App() {
  // player names
  const socket = io('http://localhost:5678');

  const navigate = useNavigate();

  const navigateToMain = () => {
    navigate('/main');
  }

  const navigateToLanding = () => {
    navigate('/');
  }

  const navigateToNewPage = () => {
    navigate('/new_game');
  }

  const navigateToJoinGame = () => {
    navigate('/join_game');
  }

  return (
    <div>
      <SocketContext.Provider value={socket}>
        <Routes>
          <Route exact path="/" element={<LandingPage nav={[navigateToNewPage, navigateToJoinGame]} socket={socket}/>}/>
          <Route exact path="/main" element={<MainPage nav={navigateToLanding} socket={socket}/>}/>
          <Route exact path="/new_game" element={<NewGamePage socket={socket}/>} />
          <Route exact path="/join_game" element={<JoinGamePage socket={socket}/>}/>
        </Routes>
      </SocketContext.Provider >
    </div>
  )
}

export default App;
