import Header from './components/header';
import { useState, useEffect } from 'react';


const NewGamePage = ({socket}) => {

    const [gameCode, setGameCode] = useState("");

    useEffect(() => {
        const getGameCodeFromServer = async () => {
            const game_code = await generateGameCode();
            setGameCode(game_code);
        }

        if (gameCode === ""){
            getGameCodeFromServer();
        }


    }, [])

    const generateGameCode = async () => {
        const response = await fetch('http://localhost:5678/new_game');
        const data = await response.json();
        return data['game_code'];
    }

    return (
        <div className='page'>
            <Header id="header_landing" />
            <div>
                <p>Generated game code is:</p>
                <p>{gameCode}</p>
                <p>Give this code to two other players to start the game.</p>
            </div>
        </div>
    )
}

export default NewGamePage;
