import Header from './components/header';
import { useState } from 'react';
import { socket } from '../context/socket';
import ButtonWithText from './components/button_with_text';

const JoinGamePage = () => {

    const [name, setName] = useState('');
    const [gameCode, setGameCode] = useState('');

    const onClick = () => {
        if (gameCode.length !== 8){
            alert('Invalid game code');
            return;
        }

        socket.emit("joinGame", {name, gameCode});

    }

    return(
        <div className='page'>
            <Header id="header_landing" />
            <label htmlFor="name" className="text_label">Please enter your name:</label>
            <input className="text_label" type="text" value={name} id="name" name="name" maxLength={10} onChange={(e) => setName(e.target.value)}></input>
            <br></br>

            <label htmlFor="game_code" className="text_label">Please enter game code to join game:</label>
            <input className="text_label" type="text" value={gameCode} id="game_code" name="game_code" maxLength={8} onChange={(e) => setGameCode(e.target.value)}></input>
            <br></br>
            <ButtonWithText text="Join game!" onClick={onClick}/>
        </div>
    )
}

export default JoinGamePage;
