import Header from './components/header';
import { useState, useEffect } from 'react';


const NewGamePage = () => {

    const [gameCode, setGameCode] = useState("");

    useEffect(() => {
        const getGameCodeFromServer = async () => {
            const game_code = await generateGameCode();
            setGameCode(game_code);
        }

        getGameCodeFromServer();
    }, [])

    generateGameCode = async () => {
        const response = await fetch('http://localhost:5678/new_game');
        const data = await response.json();
        return data['game_code'];
    }

    return (
        <div className='page'>
            <Header id="header_landing" />
            <p>{gameCode}</p>
        </div>
    )
}

export default NewGamePage;
